// Access the message content from the first choice
const content = items[0].json.choices[0].message.content;

// Use regex to extract all URLs from the text
const urls = content.match(/https:\/\/[^\s)]+/g) || [];

return [
  {
    json: {
      urls
    }
  }
];

// Get data from n8n input - handle multiple possible input structures
let inputData;
try {
  // Try to get data from the first input
  inputData = $input.first().json;

  // Handle nested structures
  if (inputData.output) {
    inputData = inputData.output;
  }

  // Handle array structures
  if (Array.isArray(inputData) && inputData.length > 0) {
    if (inputData[0].output) {
      inputData = inputData[0].output;
    } else {
      inputData = inputData[0];
    }
  }
} catch (error) {
  throw new Error(`Failed to parse input data: ${error.message}`);
}

// Validate that we have input data
if (!inputData || typeof inputData !== 'object') {
  throw new Error('No valid input data found');
}

// Required fields validation
const required = ['productName', 'productDescription', 'targetAudience', 'mainProblem', 'uniqueMechanism', 'keyBenefits', 'pricePoint'];
const missing = required.filter(field => !inputData[field] || inputData[field] === '');

if (missing.length > 0) {
  throw new Error(`Missing required fields: ${missing.join(', ')}`);
}

// Helper function to safely process arrays or comma-separated strings
function processArray(value, fallback = []) {
  if (Array.isArray(value)) {
    return value.filter(item => item && item.trim());
  }
  if (typeof value === 'string' && value.trim()) {
    return value.split(',').map(item => item.trim()).filter(item => item);
  }
  return fallback;
}

// Helper function to safely get string values
function getString(value, fallback = '') {
  return (typeof value === 'string' && value.trim()) ? value.trim() : fallback;
}

// Process and structure the data
const processedData = {
  // Core product info
  productName: getString(inputData.productName),
  productDescription: getString(inputData.productDescription),
  targetAudience: getString(inputData.targetAudience),
  mainProblem: getString(inputData.mainProblem),
  uniqueMechanism: getString(inputData.uniqueMechanism),
  keyBenefits: processArray(inputData.keyBenefits, ['Improved results', 'Better experience', 'Long-lasting solution']),
  pricePoint: getString(inputData.pricePoint),

  // URL mappings - use provided URLs or fallback to anchors
  productSiteUrl: getString(inputData.productSiteUrl, '#product-site'),
  orderUrl: getString(inputData.orderUrl, '#order'),

  // Optional fields with safe defaults
  heroImageUrl: getString(inputData.heroImageUrl, 'https://via.placeholder.com/800x400/2c3e50/ffffff?text=Product+Image'),
  claims: getString(inputData.claims),
  studies: getString(inputData.studies),
  guarantee: getString(inputData.guarantee, '100% Money Back Guarantee'),
  discount: getString(inputData.discount, '50% OFF'),

  // Generate story elements with safe defaults
  customerName: getString(inputData.customerName, 'Sarah'),
  location: getString(inputData.location, 'Texas'),
  urgencyTrigger: getString(inputData.urgencyTrigger, 'crisis situation'),

  // Headlines and hooks - prioritize custom fields, then basic fields, then auto-generate
  hook: getString(inputData.customHook || inputData.hook, 
    `${inputData.targetAudience ? inputData.targetAudience.split(' ')[0] : 'Customer'} almost lost everything because of ${inputData.mainProblem ? inputData.mainProblem.toLowerCase() : 'this problem'}.`),

  headline: getString(inputData.customHeadline || inputData.headline, 
    `${inputData.customerName || 'Mom'} Exposes ${inputData.productName} Secret That ${inputData.uniqueMechanism ? inputData.uniqueMechanism.split(' ').slice(0, 8).join(' ') : 'Changes Everything'}`),

  subheadline: getString(inputData.customSubheadline || inputData.subheadline, 
    `"I never thought ${inputData.productName ? inputData.productName.toLowerCase() : 'this product'} could ${processArray(inputData.keyBenefits)[0] ? processArray(inputData.keyBenefits)[0].toLowerCase() : 'change everything'}..."`),

  // Section headings (use custom if provided, otherwise use defaults)
  qualityHeading: getString(inputData.qualityHeading, 'Why Quality Matters'),
  featuresHeading: getString(inputData.featuresHeading, 'Key Features'),
  testimonialsHeading: getString(inputData.testimonialsHeading, 'What Our Customers Say'),
  whereToHeading: getString(inputData.whereToHeading, 'Where To Buy'),
  resultHeading: getString(inputData.resultHeading, 'Results You Can Expect'),

  // Additional section headings
  problemSectionHeading: getString(inputData.problemSectionHeading, 'The Problem You Are Facing'),
  benefitsSectionHeading: getString(inputData.benefitsSectionHeading, 'Benefits You Will Experience'),
  testimonialsSectionHeading: getString(inputData.testimonialsSectionHeading, 'Real Customer Stories'),
  whereToBuySectionHeading: getString(inputData.whereToBuySectionHeading, 'Get Yours Today'),

  // Pain points - prioritize customPainPoints, then painPoints, then generate defaults
  painPoints: processArray(inputData.customPainPoints || inputData.painPoints, [
    `Struggling with ${inputData.mainProblem ? inputData.mainProblem.toLowerCase() : 'daily challenges'}`,
    'Feeling frustrated with failed solutions',
    'Wasting time and money on products that do not work',
    'Losing confidence because nothing seems to help'
  ]),

  // Story quotes (use custom if provided, otherwise generate defaults)
  mainQuote: getString(inputData.mainQuote, 
    `"The secret is in the ${inputData.uniqueMechanism ? inputData.uniqueMechanism.toLowerCase() : 'innovative approach'}"`),

  helplessQuote: getString(inputData.helplessQuote, 
    `"I felt completely helpless dealing with ${inputData.mainProblem ? inputData.mainProblem.toLowerCase() : 'this issue'}"`),

  hopeQuote: getString(inputData.hopeQuote, 
    `"When I discovered ${inputData.productName || 'this solution'}, everything changed"`),

  // Urgency elements (use custom if provided, otherwise generate defaults)
  stockStatus: getString(inputData.stockStatus, 
    `‚óè IN STOCK: ${inputData.productName || 'Product'} Available Now`),

  limitedOffer: getString(inputData.limitedOffer, 
    `LIMITED TIME: ${inputData.discount || '50% OFF'} ${inputData.productName ? inputData.productName.toUpperCase() : 'PRODUCT'}`),

  availabilityWarning: getString(inputData.availabilityWarning, 
    '(Limited quantities available - order while supplies last)')
};

// Validate that we have all required data after processing
const processedRequired = ['productName', 'productDescription', 'targetAudience', 'mainProblem', 'uniqueMechanism'];
const stillMissing = processedRequired.filter(field => !processedData[field]);

if (stillMissing.length > 0) {
  throw new Error(`Processed data is missing required fields: ${stillMissing.join(', ')}`);
}

// Log the processed data for debugging (optional)
console.log('Processed Data:', JSON.stringify(processedData, null, 2));

return { json: processedData };

