const raw = $json.output;

function safeParse(input) {
  try {
    if (typeof input === 'string') return JSON.parse(input);
    if (typeof input === 'object') return input;
  } catch (e) {}
  return null;
}

function joinArray(arr) {
  return Array.isArray(arr) ? arr.join(', ') : '';
}

const parsed = safeParse(raw);
if (!parsed || Array.isArray(parsed)) return [];

return [
  {
    json: {
      type: "investment_criteria",
      firm_name: parsed.firm_name || "",
      investment_min: parsed.investment_min || null,
      investment_max: parsed.investment_max || null,
      industries: joinArray(parsed.industries),
      geography: joinArray(parsed.geography),
      stage_preference: joinArray(parsed.stage_preference),
      ownership_preference: joinArray(parsed.ownership_preference),
      aum_size: parsed.aum_size || null,
      revenue_requirement: parsed.revenue_requirement || null,
      annual_run_rate: parsed.annual_run_rate || null,
      ebitda_requirement: parsed.ebitda_requirement || null,
      business_model: joinArray(parsed.business_model),
      founder_profile: joinArray(parsed.founder_profile),
      esg_criteria: joinArray(parsed.esg_criteria),
      exclusions: joinArray(parsed.exclusions),
      check_size_by_round: joinArray(parsed.check_size_by_round),
      exit_strategy: joinArray(parsed.exit_strategy),
      holding_period: parsed.holding_period || null,
      investment_focus: parsed.investment_focus || "",
      special_criteria: parsed.special_criteria || ""
    }
  }
];

const raw = $json.output;

function safeParse(input) {
  try {
    if (typeof input === 'string') return JSON.parse(input);
    if (Array.isArray(input)) return input;
  } catch (e) {}
  return null;
}

const parsed = safeParse(raw);
if (!Array.isArray(parsed)) return [];

return parsed.map(entry => ({
  json: {
    type: "portfolio_company",
    ...entry
  }
}));

